import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit {
  @Input() formError = 'login or password is incorrect';

  @Output() login = new EventEmitter();
  loginForm!: FormGroup;

  formGroup: any;
  constructor(private router: Router, private authService: AuthService) {}

  submitLogin() {
    this.login.emit(this.formGroup.value);
    // this.authService.login(this.loginForm.value).subscribe({
    //   next: () => this.router.navigate(['admin']),
    //   error: (err: { message: string }) => alert(err.message),
    // });
  }

  ngOnInit(): void {
    this.loginForm = new FormGroup({
      login: new FormControl('', [Validators.required]),
      password: new FormControl('', [
        Validators.required,
        Validators.pattern(
          '^(?=.*d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$'
        ),
      ]),
    });
    if (this.authService.isLoggedIn()) {
      this.router.navigate(['/admin']);
    }
  }
  onFormChange() {
    this.formError = '';
  }
}
<div class="flex min-h-full flex-col justify-center px-6 py-12 lg:px-8">
  <div class="sm:mx-auto sm:w-full sm:max-w-sm">
    <h2
      class="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900"
    >
      Sign in to your account
    </h2>
  </div>

  <div class="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
    <form
      [formGroup]="loginForm"
      (input)="onFormChange()"
      (ngSubmit)="submitLogin()"
      class="space-y-6"
    >
      <div>
        <label
          for="login"
          class="block text-sm font-medium leading-6 text-gray-900"
          >Login</label
        >
        <div class="mt-2">
          <input
            id="login"
            formControlName="login"
            type="login"
            class="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
          />
        </div>
        <p
          *ngIf="
            this.loginForm.get('login')?.invalid &&
            this.loginForm.get('login')?.touched
          "
        >
          <small class="text-red-500">Login is required</small>
        </p>
      </div>

      <div>
        <div class="flex items-center justify-between">
          <label
            for="password"
            class="block text-sm font-medium leading-6 text-gray-900"
            >Password</label
          >
          <div class="text-sm"></div>
        </div>
        <div class="mt-2">
          <input
            id="password"
            formControlName="password"
            type="password"
            autocomplete="current-password"
            required
            class="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
          />
        </div>
        <p
          *ngIf="
            this.loginForm.get('password')?.invalid &&
            this.loginForm.get('password')?.touched
          "
        >
          <small class="text-red-500"
            >Password is required (min. length = 8)</small
          >
        </p>
      </div>
      <p class="text-red-500" *ngIf="formError">{{ formError }}</p>
      <div>
        <button
          [disabled]="loginForm.invalid || !!formError"
          type="submit"
          class="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
        >
          Sign in
        </button>
      </div>
    </form>
  </div>
</div>
